import { useState, useEffect } from "react";
import MainContainer from "../components/MainContainer";
import { useParams, useNavigate } from "react-router-dom";
import {
  Container,
  Paper,
  Button,
  TextField,
  Stack,
  IconButton,
  Typography,
} from "@mui/material";
import { toast } from "react-toastify";

// #region -----------( ICONS )-------------
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import EditIcon from "@mui/icons-material/Edit";
// #endregion -----------( ICONS )-------------

import { useBlog } from "../middleware/contextHooks";

export default function BlogDetail() {
  const { id } = useParams();
  const navigate = useNavigate();

  const {
    blogs,
    currentBlog,
    getBlogById,
    toasts,
    clearToasts,
    deleteBlog,
    updateBlog,
    getBlogs,
  } = useBlog();
  const [edit, setEdit] = useState(false);
  const [blog, setBlog] = useState(null);
  const [temp, setTemp] = useState(null);

  useEffect(() => {
    if (!blogs) {
      getBlogs();
    } else if (!currentBlog || currentBlog?._id !== id) {
      getBlogById(id);
    }

    if (currentBlog?._id === id) {
      setBlog(currentBlog);
    }

    if (toasts) {
      toasts.forEach((ele) => {
        toast(ele.message, { type: ele.type });
      });
    }
  }, [currentBlog, id, toasts, clearToasts, getBlogById, blogs, getBlogs]);

  const handleEdit = () => {
    setEdit(true);
    setTemp(blog);
  };

  const handleCancel = () => {
    setEdit(false);
    setBlog(temp);
  };

  const handleUpdate = () => {
    updateBlog(blog);
    setEdit(false);
    //setTemp(null)
  };

  const handleDelete = () => {
    deleteBlog(blog._id);
    navigate("/blogs");
  };

  const displayDisabled = () => {
    return (
      <Stack spacing={2}>
        <Stack spacing={2} direction="row">
          <TextField
            label="Title"
            value={blog?.content}
            disabled
            sx={{ flexGrow: 1 }}
          />
          <IconButton onClick={handleEdit}>
            <EditIcon />
          </IconButton>
          <IconButton onClick={handleDelete}>
            <DeleteForeverIcon />
          </IconButton>
        </Stack>
        <TextField label="Content" value={blog?.content} disabled multiline />
      </Stack>
    );
  };

  return (
    <MainContainer>
      <Container maxWidth="md" sx={{ mt: 3, mb: 5 }}>
        <Paper>
          {!edit ? (
            displayDisabled()
          ) : (
            <Stack spacing={2}>
              <TextField
                label="Title"
                name="title"
                value={blog?.title}
                onChange={(e) => setBlog({ ...blog, title: e.target.value })}
              />
              <TextField
                label="Content"
                name="content"
                value={blog?.content}
                onChange={(e) => setBlog({ ...blog, content: e.target.value })}
                multiline
                minRows={5}
                maxRows={20}
              />

              <Stack spacing={2} direction="row">
                <Button variant="contained" onClick={handleUpdate}>
                  Update
                </Button>
                <Button
                  variant="outlined"
                  sx={{ color: "primary.main" }}
                  onClick={handleCancel}
                >
                  Cancel
                </Button>
              </Stack>
            </Stack>
          )}
        </Paper>
      </Container>
    </MainContainer>
  );
}
